import React from 'react';
import TextareaAutosize from '@mui/material/TextareaAutosize';

function Textarea(props) {
  return (
    <TextareaAutosize
      style={{
        width: '100%',
        height: '120px',
        padding: '10px',
        fontFamily: 'Inter',
        fontSize: '1rem',
        lineHeight: '1.5rem',
        color: '#333333',
        border: '1px solid #CCCCCC',
        borderRadius: '8px',
        resize: 'vertical'
      }}
      {...props}
    />
  );
}

export default Textarea;
