import React, { useState } from 'react';
import './NavBar.css';
import dropdown from '../images/dropdown.svg';
import { Link } from 'react-router-dom';

const NavBar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav>
      <div className='navbar-rectangle'>
        <ul className={isMenuOpen ? 'menu-open' : ''}>
          <li className='logo'>Internship Portal</li>
          <li className='menu-toggle' onClick={handleMenuToggle}>
            <div className={`menu-icon ${isMenuOpen ? 'open' : ''}`}>
              <span></span>
              <span></span>
              <span></span>
            </div>
          </li>
          <li className={`menu-item ${isMenuOpen ? 'show' : ''}`}>
            <Link to='/' className='home'>
              Home
            </Link>
          </li>
          <li className={`menu-item ${isMenuOpen ? 'show' : ''}`}>
            <Link to='/Courses' className='courses'>
              Courses
            </Link>
          </li>
          <li className={`menu-item ${isMenuOpen ? 'show' : ''}`}>
            <Link to='/applyresearch' className='research'>
              Research
            </Link>
          </li>
          {/* Uncomment the following lines if you want to include the Interview link */}
          {/* <li className={`menu-item ${isMenuOpen ? 'show' : ''}`}>
            <Link to='/Interview' className='interview'>
              Interview
            </Link>
          </li> */}
          <li className={`menu-item ${isMenuOpen ? 'show' : ''}`}>
            <Link to='/UserProfile' className='user-name1'>
              Kashish Gandhi
            </Link>
          </li>
          <li className={`menu-item ${isMenuOpen ? 'show' : ''}`}>
            <Link to='/UserProfile' className='profile-pic'></Link>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default NavBar;
