import React from "react";
import NavBar from "../utility/NavBar";
import Login from "./Login";
import { useState } from "react";
import {
  Box,
  Button,
  TextField
} from "@mui/material";
import { styled } from "@mui/material/styles";
import { useEffect } from "react";
import Grid from "@mui/material/Grid";
import { useFormik } from "formik";
import Paper from "@mui/material/Paper";
import * as yup from "yup";
import humanAI from "../images/humanAI.png";
import "./InternshipForm.css";
import makeAnimated from "react-select/animated";
import { skillsOptions } from '../docs/data.ts';
import Select, { StylesConfig } from 'react-select';
import chroma from 'chroma-js';


const validationSchema = yup.object({
  position: yup.string("ML Engineer").required("Position is required"),
  remote: yup.string("Remote").required("Required Field"),
  duration: yup.string("1 month").required("Required Field"),
  positionsOpen: yup.string("1").required("Position is required"),
  stipend: yup.string("3000").required("Stipend is required"),
  skillsRequired: yup.string("React.js").required("Required Field"),
});

const PostInternshipForm = () => {
  const formik = useFormik({
    initialValues: {
      position: "",
      remote: "",
      duration: "",
      positionsOpen: "",
      stipend: "",
      skillsRequired: "",
    },
    validationSchema: validationSchema,
    onSubmit: (values) => {
      alert(JSON.stringify(values, null, 2));
    },
  });

  // adding event listener for responsiveness
  const [width, setWindowWidth] = useState(0);

  const color = "#4A2145";

  useEffect(() => {
    updateDimensions();
    window.addEventListener("resize", updateDimensions);
    return () => window.removeEventListener("resize", updateDimensions);
  }, []);

  const updateDimensions = () => {
    const width = window.innerWidth;
    setWindowWidth(width);
  };

  const responsiveness = { responsive: width < 1043 };
  const resp = responsiveness.responsive;

  const responsiveness2 = { responsive: width < 850 };
  const resp2 = responsiveness2.responsive;

  const Post = styled(Button)({
    backgroundColor: "#85D1A0",
    margin: "1rem",
    borderRadius: "0.3rem",
    marginLeft: "1.2rem",
    textDecoration: "none",
    padding: "12px 15px ",
    width: resp ? "22rem" : "28rem",
    height: "3.1875rem",
    color: "white",
    fontSize: "1rem",
    position: "relative",
    right: resp ? "" : "1.4rem",
    "&:hover": { backgroundColor: "#5E9387", color: "white" },
  });

  const animatedComponents = makeAnimated();

  const colourStyles: StylesConfig<ColourOption, true> = {
    control: (styles) => ({ ...styles, backgroundColor: 'white' }),
    option: (styles, { data, isDisabled, isFocused, isSelected }) => {
      const color = chroma(data.color);
      return {
        ...styles,
        backgroundColor: isDisabled
          ? undefined
          : isSelected
          ? data.color
          : isFocused
          ? color.alpha(0.1).css()
          : undefined,
        color: isDisabled
          ? '#ccc'
          : isSelected
          ? chroma.contrast(color, 'white') > 2
            ? 'white'
            : 'black'
          : data.color,
        cursor: isDisabled ? 'not-allowed' : 'default',
  
        ':active': {
          ...styles[':active'],
          backgroundColor: !isDisabled
            ? isSelected
              ? data.color
              : color.alpha(0.3).css()
            : undefined,
        },
      };
    },
    multiValue: (styles, { data }) => {
      const color = chroma(data.color);
      return {
        ...styles,
        backgroundColor: color.alpha(0.1).css(),
      };
    },
    multiValueLabel: (styles, { data }) => ({
      ...styles,
      color: data.color,
    }),
    multiValueRemove: (styles, { data }) => ({
      ...styles,
      color: data.color,
      ':hover': {
        backgroundColor: data.color,
        color: 'white',
      },
    }),
  };

  return (
    <Grid
      container
      spacing={1}
      className="postInternship_main_container"
      style={{
        // display: resp2 ? "flex" : "",
        justifyContent: "center",
        alignItems: "center",
        padding: "2rem 7rem",
        backgroundColor: "#b2ebf2",
      }}
    >
      <Grid
        item
        xs={6}
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          border: "2px solid red",
          // position: "relative",
          // top: resp2 ? "6rem" : "",
        }}
      >
        <form onSubmit={formik.handleSubmit}>
          <Box
            sx={{
              // maxWidth: "31rem",
              // width: "31rem",
              // height: "38rem",
              // border: "2px solid black",
              display: "flex",
              flexDirection: "column",
              alignItems: resp ? "center" : "flex-start",
              padding: "1rem",
              position: "relative",
              // left:'4rem',
            }}
          >
            <h1 className="formHeader">Post Internship</h1>

            <TextField
              id="position"
              type="text"
              label="Position"
              placeholder="Position"
              name="position"
              onChange={formik.handleChange}
              error={formik.touched.position && Boolean(formik.errors.position)}
              helperText={formik.touched.position && formik.errors.position}
              required
              style={{ marginBottom: "1rem", width: resp ? "42ch" : "50ch" }}
            />

            <TextField
              id="remote"
              type="text"
              label="Remote"
              placeholder="Remote"
              name="remote"
              onChange={formik.handleChange}
              error={formik.touched.remote && Boolean(formik.errors.remote)}
              helperText={formik.touched.remote && formik.errors.remote}
              required
              style={{ marginBottom: "1rem", width: resp ? "42ch" : "50ch" }}
            />

            <TextField
              id="duration"
              type="text"
              label="Duration (in months)"
              placeholder="Duration"
              name="duration"
              onChange={formik.handleChange}
              error={formik.touched.duration && Boolean(formik.errors.duration)}
              helperText={formik.touched.duration && formik.errors.duration}
              required
              style={{ marginBottom: "1rem", width: resp ? "42ch" : "50ch" }}
            />

            <TextField
              id="positionsOpen"
              type="text"
              label="Positions Open"
              placeholder="Positions Open"
              name="positionsOpen"
              onChange={formik.handleChange}
              error={
                formik.touched.positionsOpen &&
                Boolean(formik.errors.positionsOpen)
              }
              helperText={
                formik.touched.positionsOpen && formik.errors.positionsOpen
              }
              required
              style={{ marginBottom: "1rem", width: resp ? "42ch" : "50ch" }}
            />

            <TextField
              id="stipend"
              type="text"
              label="Stipend"
              placeholder="Stipend"
              name="stipend"
              onChange={formik.handleChange}
              error={formik.touched.stipend && Boolean(formik.errors.stipend)}
              helperText={formik.touched.stipend && formik.errors.stipend}
              required
              style={{ marginBottom: "1rem", width: resp ? "42ch" : "50ch" }}
            />

            <Select
              closeMenuOnSelect={false}
              components={animatedComponents}
              // defaultValue={[skillsOptions[4], skillsOptions[5]]}
              isMulti
              options={skillsOptions}
              classNames={{
                control: (state) =>
                  state.isFocused ? '#4a2145' : 'border-grey-300',
              }}
            />

            <div
            // style={{ display: "flex" }}
            >
              <Post type="submit">Post Internship</Post>
            </div>
          </Box>
        </form>
      </Grid>
      <Grid item xs={6}>
        <img src={humanAI} alt="humanAI" height="600" width="500" />
      </Grid>
    </Grid>
  );
};

const PostInternship = () => {
  return (
    <React.Fragment>
      {/* <NavBar /> */}
      {/* <Login/> */}
      <PostInternshipForm />
    </React.Fragment>
  );
};

export default PostInternship;
