import ResponsiveDrawer from "./ResponsiveDrawer";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import courseImg from "../images/courseImg.png";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import AccessTimeFilledOutlinedIcon from '@mui/icons-material/AccessTimeFilledOutlined';
import StarHalfIcon from '@mui/icons-material/StarHalf';
import * as React from "react";
import { Paper } from "@mui/material";

const Courses = () => {
  return (
    <>
      <ResponsiveDrawer />
      <div className="container" style={{ marginLeft: "270px" }}>
        <Card
          sx={{
            padding: "1rem",
            maxWidth: 345,
            width: "22rem",
            height: "23rem",
          }}
        >
          <CardMedia
            component="img"
            alt="green iguana"
            height="140"
            style={{ borderRadius: "0.5rem", marginBottom: "0" }}
            image={courseImg}
          />

          <CardContent>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                marginBottom: "1rem",
              }}
            >
              <button
                style={{
                  width: "8rem",
                  height: "1.8rem",
                  color: "#85D1A0",
                  backgroundColor: "rgb(215 236 222)",
                  borderRadius: "0.4rem",
                  margin: "0 1.5rem 0 -1rem",
                  border: "none",
                }}
              >
                Web Design
              </button>
              <span style={{ justifyContent: "flex-end" }}>
                Harvard University
              </span>
            </div>

            <Typography
              style={{
                fontFamily: "Inter",
                fontStyle: "normal",
                fontWeight: "600",
                fontSize: "1.2rem",
                textAlign:'start'
                // float:'left'
                // lineHeight: "1rem",
              }}
            >
              ReactJS with Node
            </Typography>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                margin: "1rem 0",
              }}>
                <StarHalfIcon/>
                <span style={{margin:'0 8px 0 '}}>4.5</span>
                <AccessTimeFilledOutlinedIcon/>
                <span style={{margin:'0 8px 0 2px'}}>3 months</span>
                <Button 
                style={{
                    borderRadius:'4px',
                    backgroundColor:'#85D1A0',
                    color:'white',
                    // marginLeft:'1.5rem'
                }}
                >Interested</Button>
            </div>
          </CardContent>
          {/* <CardActions>
            <Button size="small">Share</Button>
            <Button size="small">Learn More</Button>
          </CardActions> */}
        </Card>
      </div>
    </>
  );
};

export default Courses;
