import React from "react";
import NavBar from "../utility/NavBar";
import { useState } from "react";
import {
  Box,
  Button,
  TextField,
  FormControl,
  InputLabel,
  OutlinedInput,
  InputAdornment,
  IconButton,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import { useEffect } from "react";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { Link, useNavigate } from "react-router-dom";
import Grid from "@mui/material/Grid";
import EmailOutlinedIcon from "@mui/icons-material/EmailOutlined";
import "./Login.css";
import undrawImg from "../images/undraw_Welcome_.png";
import { useFormik } from "formik";
import Paper from "@mui/material/Paper";
import * as yup from "yup";
import humanAI from "../images/humanAI.png"
import Login from "./Login"

const validationSchema = yup.object({
  position: yup.string("ML Engineer").required("Position is required"),
  remote: yup.string("Remote").required("Required Field"),
  duration: yup.string("1 month").required("Required Field"),
  positionsOpen: yup.string("1").required("Position is required"),
  stipend: yup.string("3000").required("Stipend is required"),
  skillsRequired: yup.string("React.js").required("Required Field"),
});

const PostInternshipForm = () => {
  const formik = useFormik({
    initialValues: {
      position: "",
      remote: "",
      duration: "",
      positionsOpen: "",
      stipend: "",
      skillsRequired: "",
    },
    validationSchema: validationSchema,
    onSubmit: (values) => {
      alert(JSON.stringify(values, null, 2));
    },
  });

  // adding event listener for responsiveness
  const [width, setWindowWidth] = useState(0);

  const color = "#4A2145";

  useEffect(() => {
    updateDimensions();
    window.addEventListener("resize", updateDimensions);
    return () => window.removeEventListener("resize", updateDimensions);
  }, []);

  const updateDimensions = () => {
    const width = window.innerWidth;
    setWindowWidth(width);
  };

  const responsiveness = { responsive: width < 1043 };
  const resp = responsiveness.responsive;

  const responsiveness2 = { responsive: width < 850 };
  const resp2 = responsiveness2.responsive;

  return (
    <Grid
      container
      spacing={1}
      className="postInternship_main_container"
      style={{
        // display: resp2 ? "flex" : "",
        justifyContent: "center",
        alignItems: "center",
        padding:'2rem 7rem',
        backgroundColor:'#b2ebf2',
      }}
    >
      <Grid
        item
        xs={6}
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          border : '2px solid red',
          // position: "relative",
          // top: resp2 ? "6rem" : "",
        }}
      >
        <form onSubmit={formik.handleSubmit}>
        <Box
        sx={{
          // maxWidth: "31rem",
          // width: "31rem",
          // height: "38rem",
          // border: "2px solid black",
          display: "flex",
          flexDirection: "column",
          alignItems: resp ? "center" : "flex-start",
          padding: "1rem",
          position: "relative",
          // left:'4rem',
        }}
        >
          <h1 className="formHeader">Post Internship</h1>
        </Box>
        </form>
      </Grid>
      <Grid item xs={6}>
        <img src={humanAI} alt="humanAI" height='600' width='500' />
      </Grid>
    </Grid>
  );
};

const PostInternship = () => {
  return (
    <React.Fragment>
      {/* <NavBar /> */}
      {/* <Login/> */}
      <PostInternshipForm/>
    </React.Fragment>
  );
};

export default PostInternship;
