import React from "react";
import { useState } from "react";
import { Grid, Paper, Typography, Chip, Stack } from "@mui/material";
import Card from "@mui/material/Card";
import ResponsiveDrawer from "../utility/ResponsiveDrawer";
import Avatar from "@mui/material/Avatar";
import { createTheme, ThemeProvider } from '@mui/material/styles';

import gojo from "../images/gojo.png";

const ResearchPaper = () => {
  // checking local Storage after each update
  var [isDrawerOpen, setIsDrawerOpen] = useState(true);

  window.onstorage = () => {
    setIsDrawerOpen(JSON.parse(window.localStorage.getItem("drawerOpen")));
  };

  const ResearchCard = () => {
    return (
        
      <React.Fragment>
        <Grid
          container
          elevation={3}
          style={{ border: "2px solid black", width: "50rem" }}
        >
          <Grid
            xs={12}
            sm={3}
            sx={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <Avatar
              alt="Remy Sharp"
              src={gojo}
              sx={{ width: 56, height: 56 }}
            />
          </Grid>
          <Grid item md={3}>
            <Typography variant="h5" gutterBottom align="left">
              Research on xyz
            </Typography>
            <Typography variant="body2" gutterBottom align="left">
              Sridhar Iyer
            </Typography>
            <Stack direction="row" spacing={1}>
              <Chip label="primary" color="success" />
              <Chip label="success" color="info" />
            </Stack>
          </Grid>
          <Grid item md={3}></Grid>
          <Grid item md={3}></Grid>
        </Grid>
      </React.Fragment>
    );
  };
  return (
    <>
      <div>
        <ResponsiveDrawer drawerHeading={"All Research Paper"} />
        <div
          className="researchContainer"
          style={{ marginLeft: isDrawerOpen ? "245px" : "10px" }}
        >
          <ResearchCard />
        </div>
      </div>
    </>
  );
};

export default ResearchPaper;
