import ResponsiveDrawer from "./ResponsiveDrawer";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import courseImg from "../images/courseImg.png";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import * as React from "react";

const Courses = () => {
  return (
    <>
      <ResponsiveDrawer />
      <div className="container" style={{ marginLeft: "270px" }}>
        <Card sx={{ padding: "1rem",maxWidth: 345, width: "21rem", height: "23rem" }}>
          <CardMedia
            component="img"
            alt="green iguana"
            height="140"
            style={{  borderRadius: "0.5rem" , marginBottom:'0'}}
            image={courseImg}
          />
          <CardContent>
            {/* <Typography gutterBottom variant="h5" component="div">
              Lizard
            </Typography> */}
            <div 
            style={{display:'flex', alignItems:'center'}}
            >
              <button
                style={{
                  width: "8rem",
                  height: "1.5rem",
                  color: "#85D1A0",
                  backgroundColor: "rgb(215 236 222)",
                  borderRadius:'0.4rem',
                
                }}
              >
                Web Design
              </button>
              <span
              style={{justifyContent:'flex-end'}}
              >Harvard University</span>
            </div>
            <Typography variant="body2" color="text.secondary">
              Lizards are a widespread group of squamate reptiles, with over
              6,000 species, ranging across all continents except Antarctica
            </Typography>
          </CardContent>
          <CardActions>
            <Button size="small">Share</Button>
            <Button size="small">Learn More</Button>
          </CardActions>
        </Card>
      </div>
    </>
  );
};

export default Courses;
